mapper.select:
SELECT COUNT(*)
FROM "MEMBER"
WHERE MEMBER_NAME = #{memberName}
AND MEMBER_PHONE_NUMBER = #{memberPhoneNumber}

SELECT MEMBER_EMAIL
FROM "MEMBER"
WHERE MEMBER_NAME = #{memberName}
AND MEMBER_PHONE_NUMBER = #{memberPhoneNumber}

SELECT COUNT(*)
FROM "MEMBER"
WHERE MEMBER_NAME = #{memberName}
AND MEMBER_EMAIL = #{memberEmail}
AND MEMBER_PHONE_NUMBER = #{memberPhoneNumber}
