mapper.select:
SELECT COUNT(*)
FROM "MEMBER"
WHERE MEMBER_EMAIL = #{memberEmail}
AND MEMBER_NO = #{memberNo}
AND MEMBER_STATUS != '5'

SELECT COUNT(*)
FROM "SHOP"
WHERE SHOP_EMAIL = #{shopEmail}
AND SHOP_NO = #{shopNo}
AND SHOP_STATUS != '5'

SELECT MEMBER_PW
FROM "MEMBER"
WHERE MEMBER_EMAIL = #{memberEmail}
AND MEMBER_NO = #{memberNo}
AND MEMBER_STATUS != '5'

mapper.update:
UPDATE "MEMBER"
SET MEMBER_STATUS = 5
WHERE MEMBER_EMAIL = #{memberEmail}
AND MEMBER_NO = #{memberNo}
AND MEMBER_STATUS != '5'

mapper.select:
SELECT SHOP_PW
FROM "SHOP"
WHERE SHOP_EMAIL = #{shopEmail}
AND SHOP_NO = #{shopNo}
AND SHOP_STATUS != '5'

mapper.update:
UPDATE "SHOP"
SET SHOP_STATUS = 5
WHERE SHOP_EMAIL = #{shopEmail}
AND SHOP_NO = #{shopNo}
AND SHOP_STATUS != '5'

mapper.select:
SELECT COUNT(*)
FROM APPLY_HIRE
WHERE MEMBER_NO = #{memberNo}

mapper.update:
UPDATE "SHOP" SET SHOP_NAME = #{shopName}, SHOP_OWNER = #{shopOwner}, SHOP_TEL = #{shopTel},
DONG_NO = #{dongNo},
ADDRESS_DETAIL = #{addressDetail}, LAST_MODIFIED_DATE = SYSDATE WHERE SHOP_NO = #{shopNo}

mapper.select:
SELECT COUNT(*) FROM "APPLY_HIRE"
LEFT JOIN "HIRE" USING (HIRE_NO)
WHERE MEMBER_NO=#{memberNo}
